class Player{
  boolean isUp, isDown, isLeft, isRight;
  float speed = 5;
  float xVel, yVel, xRPos, yRPos;
  int pxPos, pyPos, xPos, yPos;
  Player(float xPos, float yPos){
    this.xPos = PApplet.parseInt(xPos);
    this.yPos = PApplet.parseInt(yPos);
  } 
  public void display(){
  }
  public void setMove(int k, boolean isPressed){
    switch(k){
      case 'A':
        isLeft = isPressed;
        break;
      case 'S':
        isDown = isPressed;
        break;
      case 'D':
        isRight = isPressed;
        break;
      case 'W':
        isUp = isPressed;
        break;
    }
  }
  public void playerMove(){
    if(isRight){
      xVel = speed;
    }
    if(isLeft){
      xVel = -speed;
    }
    if(isUp){
      yVel = -speed;
    }
    if(isDown){
      yVel = speed;
    }
  }
  public boolean checkCollision(){
    if(xRPos > speed && xRPos < map.width-speed && /*yRPos > speed &&*/ yRPos < map.height-speed){
      if(brightness(map.pixels[PApplet.parseInt(yPos + height/2)*map.width+PApplet.parseInt(xPos + width/2)]) > 210){
        if(xPos > pxPos) xPos--;
        if(xPos < pxPos) xPos++;
        if(yPos > pyPos) yPos--;
        if(yPos < pyPos) yPos++;
        return true;
      }else{
        pxPos = xPos;
        pyPos = yPos;
        return false;
      }
    }else{
      return false;
    }
  }
  public void updateMovement(){
    for(int i = 0; i < xVel; i++){
      xPos++;
      if(checkCollision()) break;
    }
    for(int i = 0; i > xVel; i--){
      xPos--;
      if(checkCollision()) break;
    }
    for(int i = 0; i < yVel; i++){
      yPos++;
      if(checkCollision()) break;
    }
    for(int i = 0; i > yVel; i--){
      yPos--;
      if(checkCollision()) break;
    }
    xVel = 0;
    yVel = 0;
  }
  public void translateMove(){
    translate(-xPos, -yPos);
  }
  public void playerEffects(){
    
  }
  public void updateRealPosition(){
    xRPos = xPos + width/2;
    yRPos = yPos + height/2;
  }
}
